import React, { useEffect, useState } from "react";
import TaskItem from "./TaskItem";
import data from "../data/checklist.json";

const Checklist = () => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const stored = localStorage.getItem("uiux-checklist");
    setTasks(stored ? JSON.parse(stored) : data);
  }, []);

  useEffect(() => {
    localStorage.setItem("uiux-checklist", JSON.stringify(tasks));
  }, [tasks]);

  const toggleTask = (index) => {
    const updated = [...tasks];
    updated[index].done = !updated[index].done;
    setTasks(updated);
  };

  return (
    <div className="max-w-2xl mx-auto mt-10">
      <h1 className="text-3xl font-bold mb-6 text-center">UI/UX Checklist âœ…</h1>
      {tasks.map((task, index) => (
        <TaskItem key={index} task={task} index={index} toggleTask={toggleTask} />
      ))}
    </div>
  );
};

export default Checklist;
