import React, { useEffect, useState } from "react";
import TaskItem from "./TaskItem";
import data from "../data/checklist.json";

function Checklist() {
  // TEMP: Force clear old localStorage (remove this after confirming it works)
  useEffect(() => {
    localStorage.removeItem("uiux-checklist");
  }, []);

  // Load tasks from localStorage or fallback to checklist.json
  const [tasks, setTasks] = useState(() => {
    const saved = localStorage.getItem("uiux-checklist");
    return saved ? JSON.parse(saved) : data;
  });

  // Save tasks to localStorage on every update
  useEffect(() => {
    localStorage.setItem("uiux-checklist", JSON.stringify(tasks));
  }, [tasks]);

  // Toggle done/undone
  const toggleTask = (index) => {
    const updated = [...tasks];
    updated[index].done = !updated[index].done;
    setTasks(updated);
  };

  // Reset all tasks to unchecked
  const resetChecklist = () => {
    const reset = tasks.map((t) => ({ ...t, done: false }));
    setTasks(reset);
  };

  // Group tasks by category
  const grouped = tasks.reduce((acc, task, index) => {
    if (!acc[task.category]) acc[task.category] = [];
    acc[task.category].push({ ...task, index });
    return acc;
  }, {});

  return (
    <div className="max-w-3xl mx-auto bg-white p-6 rounded-lg shadow-lg">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-2xl font-bold text-gray-800">UI/UX Checklist âœ…</h1>
        <button
          onClick={resetChecklist}
          className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded"
        >
          Reset All
        </button>
      </div>

      {Object.entries(grouped).map(([category, items]) => (
        <div key={category} className="mb-6">
          <h2 className="text-xl font-semibold text-blue-700 mb-2">{category}</h2>
          {items.map(({ index, ...task }) => (
            <TaskItem
              key={index}
              task={task}
              index={index}
              toggleTask={toggleTask}
            />
          ))}
        </div>
      ))}
    </div>
  );
}

export default Checklist;
