import React, { useEffect, useState } from "react";
import TaskItem from "./TaskItem";
import data from "../data/checklist.json";
import html2pdf from "html2pdf.js";

function Checklist() {
  const [tasks, setTasks] = useState(() => {
    const saved = localStorage.getItem("uiux-checklist");
    return saved ? JSON.parse(saved) : data;
  });

  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    localStorage.setItem("uiux-checklist", JSON.stringify(tasks));
  }, [tasks]);

  const toggleTask = (index) => {
    const updated = [...tasks];
    updated[index].done = !updated[index].done;
    setTasks(updated);
  };

  const resetChecklist = () => {
    const reset = tasks.map((t) => ({ ...t, done: false }));
    setTasks(reset);
  };

  const grouped = tasks.reduce((acc, task, index) => {
    if (!acc[task.category]) acc[task.category] = [];
    acc[task.category].push({ ...task, index });
    return acc;
  }, {});

  const completed = tasks.filter((t) => t.done).length;
  const total = tasks.length;
  const percent = Math.round((completed / total) * 100);

  const exportToPDF = () => {
    const element = document.getElementById("checklist-content");
    html2pdf().from(element).save("checklist.pdf");
  };

  return (
    <div className={`${darkMode ? "bg-gray-900 text-white" : "bg-white text-gray-900"} min-h-screen p-6`}>
      <div className="max-w-3xl mx-auto">
        <div className="flex justify-between items-center mb-4">
          <div className="flex items-center gap-4">
            <h1 className="text-2xl font-bold">UI/UX Checklist ‚úÖ</h1>
            <button
              onClick={() => setDarkMode(!darkMode)}
              className="px-3 py-1 text-sm bg-gray-200 dark:bg-gray-700 text-black dark:text-white rounded hover:bg-gray-300"
            >
              {darkMode ? "‚òÄÔ∏è Light Mode" : "üåô Dark Mode"}
            </button>
          </div>
          <div className="flex gap-2">
            <button
              onClick={resetChecklist}
              className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded"
            >
              Reset All
            </button>
            <button
              onClick={exportToPDF}
              className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded"
            >
              Export PDF
            </button>
          </div>
        </div>

        {/* Progress bar */}
        <div className="w-full bg-gray-300 dark:bg-gray-700 rounded-full h-4 mb-6">
          <div
            className="bg-green-500 h-4 rounded-full"
            style={{ width: `${percent}%` }}
          ></div>
          <p className="text-sm mt-1 text-center">{percent}% completed</p>
        </div>

        <div id="checklist-content">
          {Object.entries(grouped).map(([category, items]) => (
            <div key={category} className="mb-6">
              <h2 className="text-xl font-semibold text-blue-700 dark:text-blue-300 mb-2">
                {category}
              </h2>
              {items.map(({ index, ...task }) => (
                <TaskItem
                  key={index}
                  task={task}
                  index={index}
                  toggleTask={toggleTask}
                />
              ))}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Checklist;
